// Fixed N8N Form Validation Code Node
const inputData = $input.all();

// Improved validation functions
const validators = {
  // Full Name validation (2-50 characters, letters and spaces only)
  validateFullName: (name) => {
    if (!name || typeof name !== 'string') return false;
    const nameRegex = /^[a-zA-Z\s]{2,50}$/;
    return nameRegex.test(name.trim());
  },

  // Email validation
  validateEmail: (email) => {
    if (!email || typeof email !== 'string') return false;
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email.trim());
  },

  // Company Name validation (2-100 characters)
  validateCompanyName: (company) => {
    if (!company || typeof company !== 'string') return false;
    const trimmed = company.trim();
    return trimmed.length >= 2 && trimmed.length <= 100;
  },

  // FIXED Website URL validation
  validateWebsiteURL: (url) => {
    if (!url || typeof url !== 'string') return false;

    const trimmed = url.trim();
    if (trimmed.length === 0) return false;

    // Very simple and reliable validation
    // Must contain at least one dot and have valid characters
    const basicPattern = /^[a-zA-Z0-9][a-zA-Z0-9.-]*\.[a-zA-Z]{2,}$/;

    // Remove protocol if present for pattern matching
    let testUrl = trimmed;
    if (testUrl.startsWith('http://')) {
      testUrl = testUrl.substring(7);
    } else if (testUrl.startsWith('https://')) {
      testUrl = testUrl.substring(8);
    }

    // Basic pattern check
    if (!basicPattern.test(testUrl)) {
      return false;
    }

    // Try URL constructor as final validation
    try {
      const fullUrl = testUrl.startsWith('http') ? testUrl : `https://${testUrl}`;
      new URL(fullUrl);
      return true;
    } catch (e) {
      // If URL constructor fails, still return true if basic pattern passed
      // This handles cases where the URL might be valid but not accessible
      return true;
    }
  },

  // Company Size validation (positive integer)
  validateCompanySize: (size) => {
    const num = parseInt(size);
    return !isNaN(num) && num > 0 && num <= 1000000;
  },

  // Budget validation (positive number)
  validateBudget: (budget) => {
    const num = parseFloat(budget);
    return !isNaN(num) && num > 0 && num <= 10000000;
  },

  // Phone validation (flexible format, 8-15 digits)
  validatePhone: (phone) => {
    if (!phone && phone !== 0) return false;

    // Convert to string if it's a number
    const phoneStr = phone.toString();

    // Remove all non-digit characters
    const cleanPhone = phoneStr.replace(/\D/g, '');

    // Check if it has 8-15 digits
    return cleanPhone.length >= 8 && cleanPhone.length <= 15;
  }
};

// Process each input item
const results = inputData.map((item, index) => {
  const data = item.json;

  // Extract field values using exact field names from your form
  const fullName = data['Full Name'];
  const email = data['Email'];
  const companyName = data['Company Name'];
  const websiteURL = data['Company Website URL'];
  const companySize = data['Company Size'];
  const budget = data['Your Budget'];
  const phone = data['Phone'];

  // Perform validations
  const validationResults = {
    fullName: {
      value: fullName,
      isValid: validators.validateFullName(fullName),
      error: !validators.validateFullName(fullName) ? 'Full name must be 2-50 characters, letters and spaces only' : null
    },
    email: {
      value: email,
      isValid: validators.validateEmail(email),
      error: !validators.validateEmail(email) ? 'Please enter a valid email address' : null
    },
    companyName: {
      value: companyName,
      isValid: validators.validateCompanyName(companyName),
      error: !validators.validateCompanyName(companyName) ? 'Company name must be 2-100 characters' : null
    },
    websiteURL: {
      value: websiteURL,
      isValid: validators.validateWebsiteURL(websiteURL),
      error: !validators.validateWebsiteURL(websiteURL) ? 'Please enter a valid website URL' : null
    },
    companySize: {
      value: companySize,
      isValid: validators.validateCompanySize(companySize),
      error: !validators.validateCompanySize(companySize) ? 'Company size must be a positive number' : null
    },
    budget: {
      value: budget,
      isValid: validators.validateBudget(budget),
      error: !validators.validateBudget(budget) ? 'Budget must be a positive number' : null
    },
    phone: {
      value: phone,
      isValid: validators.validatePhone(phone),
      error: !validators.validatePhone(phone) ? 'Phone number must contain 8-15 digits' : null
    }
  };

  // Check if all validations passed
  const allValid = Object.values(validationResults).every(field => field.isValid);

  // Collect all errors in different formats
  const errors = Object.entries(validationResults)
    .filter(([key, field]) => !field.isValid)
    .map(([key, field]) => ({ field: key, error: field.error }));

  // Combined error messages
  const combinedErrors = {
    // Single string with all errors separated by commas
    errorString: errors.map(e => e.error).join(', '),

    // Single string with field names and errors
    detailedErrorString: errors.map(e => `${e.field}: ${e.error}`).join('; '),

    // HTML formatted errors for email templates
    htmlErrors: errors.map(e => `<li><strong>${e.field}:</strong> ${e.error}</li>`).join(''),
    htmlErrorList: errors.length > 0 ? `<ul>${errors.map(e => `<li><strong>${e.field}:</strong> ${e.error}</li>`).join('')}</ul>` : '',

    // Array of field names that failed
    failedFields: errors.map(e => e.field),

    // Count of errors
    errorCount: errors.length
  };

  // Return validation result
  return {
    json: {
      ...data,
      validation: {
        isValid: allValid,
        errors: errors, // Original array format
        combined: combinedErrors, // New combined formats
        details: validationResults
      }
    }
  };
});

return results;

